        #     Write-Host -ForegroundColor Green ("'{0}' is of expected type '{1}'" -f $thisfile, $thistype)
        # }
        $attrib = [IO.FileInfo]::new($thisfile).Attributes
        if ($attrib -ne -1 -and $attrib.HasFlag([IO.FileAttributes]::Directory))
        {
            Write-Host -ForegroundColor Red ("'{0}' is unexpectedly reported as a directory (attrib = 0x{1:x})" -f $thisfile, $attrib)
            Exit(1)
        }
        else
