            Write-Host -ForegroundColor Green ("'{0}' is expectedly NOT a directory (attrib = 0x{1:x})" -f $thisfile, $attrib)
        }

    }
    catch
    {
        Write-Host -ForegroundColor Red ("EXCEPTION: thisfile='{0}' {1}" -f $thisfile, $error[0])
        Exit(1)
    }
}

function CleanSlate
{
    foreach ($varname in @('thisfile', 'thisitem', 'otherfile', 'otheritem', 're', 'removedir', 'founditem'))
    {
        Remove-Variable -Name $varname -ErrorAction SilentlyContinue
    }
}

