$otherfile = $thisfile -replace $finddir_re, $otherdir
$founditem = Get-Item -LiteralPath $thisfile
$re = [regex]::Escape(($founditem.Name -replace '\s+\(.*UTC\)', '')) + '|' + [regex]::Escape(($founditem.BaseName -replace '\s+\(.*UTC\)', '')) + '\s+\(.*UTC\)' + [regex]::Escape($founditem.Extension)
$otherfiles = (Get-ChildItem -file -force -literalpath (split-path -parent $otherfile) -ErrorAction SilentlyContinue | Where-Object { $_.Name -match $re })

if ($null -ne $otherfiles)
{
    foreach ($otherfile in $otherfiles.FullName)
    {
        ProcessFile
    }
}

# -------------------------------------------------------------------------------------------------------
Write-Host -ForegroundColor Magenta ("ProcessFile invoked from foreach loop within Foreach-Object loop")
CleanSlate
@('a') | ForEach-Object {

    $thisfile = $finddir + '\2015\02\15\IMG_0751 (2021_03_14 18_18_52 UTC).JPG'
    $founditem = Get-Item -LiteralPath $thisfile
    $re = [regex]::Escape(($founditem.Name -replace '\s+\(.*UTC\)', '')) + '|' + [regex]::Escape(($founditem.BaseName -replace '\s+\(.*UTC\)', '')) + '\s+\(.*UTC\)' + [regex]::Escape($founditem.Extension)
    $otherfile = $thisfile -replace $finddir_re, $otherdir
    $otherfiles = (Get-ChildItem -file -force -literalpath (split-path -parent $otherfile) -ErrorAction SilentlyContinue | Where-Object { $_.Name -match $re })

    if ($null -ne $otherfiles)
    {
        foreach ($otherfile in $otherfiles.FullName)
        {
            ProcessFile
        }
    }

}

# -------------------------------------------------------------------------------------------------------
Write-Host -ForegroundColor Magenta ("original logic")
CleanSlate
Get-ChildItem -File -Force -Recurse -LiteralPath $finddir -ErrorAction Stop | ForEach-Object {
    $founditem = $_
    $thisfile = $founditem.FullName
    $otherfile = $thisfile -replace $finddir_re, $otherdir
    if (!(Test-Path -literalpath $otherfile))
    {
        $re = [regex]::Escape(($founditem.Name -replace '\s+\(.*UTC\)', '')) + '|' + [regex]::Escape(($founditem.BaseName -replace '\s+\(.*UTC\)', '')) + '\s+\(.*UTC\)' + [regex]::Escape($founditem.Extension)

        $otherfiles = (Get-ChildItem -file -force -literalpath (split-path -parent $otherfile) -ErrorAction SilentlyContinue | Where-Object { $_.Name -match $re })

        if ($null -ne $otherfiles)
        {
            foreach ($otherfile in $otherfiles.FullName)
            {
                Write-Host -ForegroundColor Blue ("Found otherfile '{0}'" -f $otherfile)
                ProcessFile
            }
        }
    }
    else
    {
        Write-Host -ForegroundColor Blue ("otherfile '{0}' exists" -f $otherfile)
        ProcessFile
    }


}


