    return result
}

constructAlias(parsedLine) throws argException {
    // create options out of parsed line i.e. a collection object. This might throw an exception
    options = (ScopedItemOptions)Enum.Parse(typeof(ScopedItemOptions), values[3], true);

    // add description (which is the second term of parsedLine) if not null or empty, just like old code

    if (!string.IsNullOrEmpty(values[2]))
        {
            newAlias.Description = values[2];
        }

    // create and return the the actual alias object
    return new AliasInfo(parsedLine, options)
}

lineShouldBeSkipped(line) {
    //if line is empty or a comment, return true
    return line.Length == 0 || OnlyContainsWhitespace(line) || line[0] == '#'
}

