                        "@{Name=Cmc.Installer.Core.Prerequisite.DotNetPrerequisite; Version=4.5; MinimumVersion=4.5.2}",
                        "@{Name=Cmc.Installer.Core.Prerequisite.AdminPrerequisite; Version=1.0}",
