using namespace std;
class ustring : public std::basic_string<unsigned char> {
public:
    using std::basic_string<unsigned char>::basic_string;  // import constructors
 
    ustring(const std::string &str) : basic_string(str.begin(), str.end()) {}
    ustring(const char *str) : ustring(std::string(str)) {}
   
    ustring &operator+=(const ustring &str) { append(str.begin(), str.end()); return *this; }
   
    operator std::string() const { return std::string(begin(), end()); }
};
 
ustring operator+(const ustring &left, const ustring &right) { ustring ret(left); return ret += right; }
 
 
static ustring str2numbers(const std::vector<unsigned char> &bin);
 
//  start-process -filepath C:\path\this.exe  -ArgumentList @('test')
