#$executioncontext.InvokeCommand.CommandNotFoundAction = $null;
$executioncontext.InvokeCommand.CommandNotFoundAction = {
    $commandName = $_.CommandName.Replace("get-", "");

    if($regEx.IsMatch($commandName))
    {
        $matches = $regEx.Match($commandName);
        $amount = $matches.Groups["amount"].Value;
        $unit = $matches.Groups["unit"].Value;
        $direction= $matches.Groups["direction"].Value;

        $__agoCommand = "function tmpDummy(){__ago $amount `"$unit`" `"$direction`";}";
        $scriptBlockString =  $__agoCommand +"(tmpDummy)";

       $scriptBlock = [ScriptBlock]::Create($scriptBlockString);

        New-Module -Name "dummy"  -ScriptBlock $scriptBlock

        $tmpCmd = Get-Command 'tmpDummy'

        $_.Command = $tmpCmd;
        $_.StopSearch = $true
     }
}
