So, I have found the quickest way so far is shown below but the exercise has been useful thanks for your help.

(if there is a better way then please advise)

  function Get-Dll
  {
    param
    (
      [System.IO.FileSystemInfo]
      [Parameter(Mandatory = $True, ValueFromPipeline = $True, HelpMessage = 'Data to filter')]
      $InputObject
    )
    process
    {
      if ($InputObject.extension -eq '.dll' -and ($InputObject.VersionInfo.ProductVersion -or $InputObject.VersionInfo.FileVersion))
      {
        $Result = [Ordered]@{
          Name            = $InputObject.Name
          ProductVersion  = $InputObject.VersionInfo.ProductVersion
          FileVersion     = $InputObject.VersionInfo.FileVersion
          FileDescription = $InputObject.VersionInfo.FileDescription
          FullName        = $InputObject.FullName
        }
        New-Object -TypeName PSObject -Property $Result
      }
    }
  }
$PFPaths = 'C:\Program Files*\', 'C:\Windows\'
$DLLs=(Get-Item -Path $PFPaths |
    Get-ChildItem -Recurse -ErrorAction SilentlyContinue -File |
    Get-Dll)
