        /// <summary>
        /// The following is the definition of the input parameter "Description".
        /// A descriptive comment (48 characters).
        /// </summary>
        [Parameter(Mandatory = true)]
        [ValidateNotNull]
        [ValidateLength(0, 48)]
        public string Description
